const c=document.querySelectorAll(".tabs-link"),o=document.querySelectorAll(".tabs-tab");c.forEach(t=>{let s=t.getAttribute("data-tabs-target");t.addEventListener("click",r=>{c.forEach(a=>a.removeAttribute("data-active")),r.target.toggleAttribute("data-active"),o.forEach(a=>a.classList.add("hidden")),document.querySelector("#"+s).classList.remove("hidden"),location.hash="#"+s.substring(4),sessionStorage.setItem("tab",location.hash)})});let e=location.hash;if(e)document.querySelector(".tabs-link[data-tabs-target='tab-"+e.substring(1)+"']").click();else if(e=sessionStorage.getItem("tab"),e){const t=document.querySelector(".tabs-link[data-tabs-target='tab-"+e.substring(1)+"']");t&&t.click()}
